package com.liceu.server.system.v2.post

import com.google.common.truth.Truth
import com.liceu.server.data.PostRepository
import com.liceu.server.system.TestSystem
import org.junit.jupiter.api.Disabled
import org.junit.jupiter.api.Test
import org.springframework.beans.factory.annotation.Autowired
import org.springframework.boot.test.web.client.exchange
import org.springframework.http.HttpEntity
import org.springframework.http.HttpHeaders
import org.springframework.http.HttpMethod
import org.springframework.http.HttpStatus

class TestUpdateDocument: TestSystem("/v2/post") {
    @Autowired
    lateinit var postRepo: PostRepository

    @Test
    fun updateDocument_validPDFDocument_verifyPost(){
        val headers = HttpHeaders()
        headers["API_KEY"] = apiKey
        headers["Authorization"] = testSetup.USER_1_ACCESS_TOKEN
        val entity = HttpEntity(hashMapOf(
                "documentTitle" to "arquivoSobreBiologia",
                "documentData" to "data:application/pdf;base64,"
        ), headers)
        val response = restTemplate.exchange<Void>("$baseUrl/${testSetup.POST_ID_1}/docs", HttpMethod.PUT, entity)
        Truth.assertThat(response.statusCode).isEqualTo(HttpStatus.OK)
        val changedPost = postRepo.findById(testSetup.POST_ID_1).get()
        Truth.assertThat(changedPost.document?.title).isEqualTo("arquivoSobreBiologia")
        Truth.assertThat(changedPost.document?.type).isEqualTo("pdf")
        Truth.assertThat(changedPost.document?.documentURL).isEqualTo("https://storage.cloud.google.com/liceu-post-documents/arquivoSobreBiologia.pdf")

    }

