class TestUpdateDocument: TestSystem("/v2/post") {
    @Autowired
    lateinit var postRepo: PostRepository

    @Test
    fun updateDocument_validPDFDocument_verifyPost(){
        val headers = HttpHeaders()
        headers["API_KEY"] = apiKey
        headers["Authorization"] = testSetup.USER_1_ACCESS_TOKEN
        val entity = HttpEntity(hashMapOf(
                "documentsData" to listOf(
                        hashMapOf(
                                "documentTitle" to "arquivoSobreBiologia",
                                "documentData" to "data:application/pdf;base64,"
                        ),
                        hashMapOf(
                                "documentTitle" to "arquivoSobreGeografia",
                                "documentData" to "data:text/plain;base64,"
                        )
                )), headers)
        val response = restTemplate.exchange<Void>("$baseUrl/${testSetup.POST_ID_1}/docs", HttpMethod.PUT, entity)
        Truth.assertThat(response.statusCode).isEqualTo(HttpStatus.OK)
        val changedPost = postRepo.findById(testSetup.POST_ID_1).get()
        Truth.assertThat(changedPost.documents?.size).isEqualTo(2)
        Truth.assertThat(changedPost.documents?.get(0)?.title).isEqualTo("arquivoSobreBiologia")
        Truth.assertThat(changedPost.documents?.get(0)?.type).isEqualTo("pdf")
        Truth.assertThat(changedPost.documents?.get(0)?.documentURL).isNotNull()
        Truth.assertThat(changedPost.documents?.get(1)?.title).isEqualTo("arquivoSobreGeografia")
        Truth.assertThat(changedPost.documents?.get(1)?.type).isEqualTo("txt")
        Truth.assertThat(changedPost.documents?.get(1)?.documentURL).isNotNull()
    }

